      <button class=${classMap(classes)} ?disabled=${this.disabled} @click=${this.onClick}>
        ${this.iconLeft}
        <w3m-text variant="small-regular" color=${textColor}>
          <slot></slot>
        </w3m-text>
        ${this.iconRight}
      </button>
    `
  }
}

